import styled from 'styled-components';
   
export const Box = styled.div`
  padding: 5px 2px;
  background: white;
  position: absolute;
  top: 30px;
  width: 100%;
  
   
  @media (max-width: 100%) {
    padding: 30px 15px;
  }
`;
   
export const Container = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: left;
    max-width: 100%;
    margin: 0 auto;
    position: relative;
    top: 0;
    /* background: red; */
`
   
export const Column = styled.div`
  display: flex;
  flex-direction: column;
  text-align: left;
  margin-left: 20px;
`;

export const Row = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, 
                         minmax(185px, 1fr));
  grid-gap: 20px;
   
  @media (max-width: 1000px) {
    grid-template-columns: repeat(auto-fill, 
                           minmax(200px, 1fr));
  }
`;
   
export const FooterLink = styled.a`
  color: grey;
  margin-bottom: 8px;
  font-size: 12px;
  text-decoration: none;
   
  &:hover {
      color: yellow;
      transition: 200ms ease-in;
  }
`;
   
export const Heading = styled.p`
  font-size: 14px;
  color: grey;
  margin-bottom: 10px;
  font-weight: bold;
`;

   
export const Imgaging = styled.img`
   position: relative;
   top: 10px;
   left: 70px;
   width: 240px;
`;

export const FirstColumn = styled.div`
   display: flex;
   flex-direction: column;
text-align: left;
margin-left: 20px;
`;
