import styled from 'styled-components';

export const BoxFooter = styled.div`
  padding: 0px 0px;
  background: white;
  justify-content: center;
  position: relative;
  top: 40px;
  left: 10%;
  width: 80%;
  border-style: ridge;
     
  @media (max-width: 100%) {
    padding: 0px 0px;
  }
`;

export const ContainerFooter = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    max-width: 100%;
    margin: 0 auto;
    position: relative;
    top: 0px;
    left: 0px;
    /* background: red; */
`;

export const ColumnFooter = styled.div`
  display: flex;
  flex-direction: column;
  text-align: right;
  margin-left: 0px;
`;

export const RowFooter = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, 
                         minmax(185px, 1fr));
  grid-gap: 0px;
   
  @media (max-width: 1000px) {
    grid-template-columns: repeat(auto-fill, 
                           minmax(200px, 1fr));
  }
`;

export const HeadingFooter = styled.a`
  font-size: 17px;
  color: grey;
  position: absolute;
  left: 380px;
  margin-bottom: 0px;
  font-weight: bold;

  &:hover {
    color: #ffe302;
    transition: 200ms ease-in;
}
`;

export const ImgagingFooter = styled.img`
   position: relative;
   top: 0px;
   left: 0px;
   bottom: 0px;
   width: 350px;
   height: 250px
`;
