import styled from 'styled-components';

export const BoxFooter = styled.div`
  padding: 5px 2px;
  background: white;
  position: relative;
  top: 30px;
  width: 80%;
  left: 40px;
     
  @media (max-width: 100%) {
    padding: 30px 15px;
  }
`;

export const ContainerFooter = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: left;
    max-width: 100%;
    margin: 0 auto;
    position: relative;
    top: 30px;
    left: 400px;
    /* background: red; */
`;

export const ColumnFooter = styled.div`
  display: flex;
  flex-direction: column;
  text-align: left;
  margin-left: 0px;
`;

export const RowFooter = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, 
                         minmax(185px, 1fr));
  grid-gap: 20px;
   
  @media (max-width: 1000px) {
    grid-template-columns: repeat(auto-fill, 
                           minmax(200px, 1fr));
  }
`;

export const HeadingFooter = styled.a`
  font-size: 17px;
  color: grey;
  margin-bottom: 10px;
  font-weight: bold;

  &:hover {
    color: #ffe302;
    transition: 200ms ease-in;
}
`;

export const ImgagingFooter = styled.img`
   position: relative;
   top: 10px;
   left: 70px;
   width: 240px;
`;
